import React, { useState, useEffect } from 'react';
import './App.css';

// Weather Icons mapping
const weatherIcons = {
  '01d': '‚òÄÔ∏è', '01n': 'üåô',
  '02d': '‚õÖ', '02n': '‚òÅÔ∏è',
  '03d': '‚òÅÔ∏è', '03n': '‚òÅÔ∏è',
  '04d': '‚òÅÔ∏è', '04n': '‚òÅÔ∏è',
  '09d': 'üåßÔ∏è', '09n': 'üåßÔ∏è',
  '10d': 'üå¶Ô∏è', '10n': 'üåßÔ∏è',
  '11d': '‚õàÔ∏è', '11n': '‚õàÔ∏è',
  '13d': '‚ùÑÔ∏è', '13n': '‚ùÑÔ∏è',
  '50d': 'üå´Ô∏è', '50n': 'üå´Ô∏è'
};

// Format time to 12-hour format
const formatTime = (timeString) => {
  if (!timeString) return '';
  const time = timeString.split(' ')[1]; // Get only the time part
  const [hours, minutes] = time.split(':');
  const hour = parseInt(hours, 10);
  const ampm = hour >= 12 ? 'PM' : 'AM';
  const hour12 = hour % 12 || 12;
  return `${hour12}:${minutes} ${ampm}`;
};

function App() {
  const [weather, setWeather] = useState(null);
  const [city, setCity] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const API_KEY = '9a983165173b4de69a1170742252411';
  const API_URL = `http://api.weatherapi.com/v1/current.json?key=${API_KEY}&aqi=yes`;

  const fetchWeather = async (cityName) => {
    if (!cityName) return;
    
    setLoading(true);
    setError('');
    
    try {
      const response = await fetch(`${API_URL}&q=${encodeURIComponent(cityName)}`);
      const data = await response.json();
      
      if (!data.error) {
        const weatherData = {
          city: data.location.name,
          country: data.location.country,
          temp: Math.round(data.current.temp_c),
          description: data.current.condition.text,
          icon: `https:${data.current.condition.icon}`,
          humidity: data.current.humidity,
          wind: data.current.wind_kph,
          wind_dir: data.current.wind_dir,
          pressure: data.current.pressure_mb,
          visibility: data.current.vis_km,
          feels_like: Math.round(data.current.feelslike_c),
          last_updated: data.current.last_updated,
          is_day: data.current.is_day ? 'day' : 'night'
        };
        
        setWeather(weatherData);
        setCity(''); // Clear input after successful search
      } else {
        setError(data.error.message || 'City not found. Please try again.');
      }
    } catch (err) {
      setError('Failed to fetch weather data');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchWeather(city);
  };

  // Empty dependency array means this effect runs once on mount
  useEffect(() => {
    // No default city fetch on initial load
  }, []);

  // Get weather emoji based on icon code
  const getWeatherEmoji = (iconUrl) => {
    if (!iconUrl) return 'üå°Ô∏è';
    const iconCode = iconUrl.split('/').pop().split('.')[0];
    return weatherIcons[iconCode] || 'üå°Ô∏è';
  };

  return (
    <div className="app">
      <div className="container">
        <h1>Weather Forecast</h1>
        
        <form onSubmit={handleSubmit} className="search-box">
          <input
            type="text"
            placeholder="Enter city name..."
            value={city}
            onChange={(e) => setCity(e.target.value)}
            disabled={loading}
          />
          <button type="submit" disabled={loading || !city.trim()}>
            {loading ? (
              <span className="loading-dots">Searching</span>
            ) : (
              'Get Weather'
            )}
          </button>
        </form>

        {error && (
          <div className="error">
            <span>‚ö†Ô∏è</span> {error}
          </div>
        )}

        {loading && !weather ? (
          <div className="loading">
            <div className="loading-spinner"></div>
            <p>Fetching weather data...</p>
          </div>
        ) : weather ? (
          <div className="weather-card">
            <div className="location">
              <h2>{weather.city}, {weather.country}</h2>
              <p>Last updated: {formatTime(weather.last_updated)}</p>
            </div>
            
            <div className="weather-main">
              <div className="weather-icon">
                <img 
                  src={weather.icon} 
                  alt={weather.description}
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.style.display = 'none';
                    e.target.parentNode.innerHTML = getWeatherEmoji(weather.icon);
                  }}
                />
              </div>
              <div className="temp">{weather.temp}¬∞</div>
            </div>
            
            <div className="weather-desc">
              {weather.description}
            </div>
            
            <div className="weather-details">
              <div className="detail-item">
                <div className="icon">üå°Ô∏è</div>
                <div className="info">
                  <span className="value">{weather.feels_like}¬∞</span>
                  <span className="label">Feels Like</span>
                </div>
              </div>
              <div className="detail-item">
                <div className="icon">üíß</div>
                <div className="info">
                  <span className="value">{weather.humidity}%</span>
                  <span className="label">Humidity</span>
                </div>
              </div>
              <div className="detail-item">
                <div className="icon">üí®</div>
                <div className="info">
                  <span className="value">{weather.wind} km/h</span>
                  <span className="label">{weather.wind_dir} Wind</span>
                </div>
              </div>
              <div className="detail-item">
                <div className="icon">üìä</div>
                <div className="info">
                  <span className="value">{weather.pressure} mb</span>
                  <span className="label">Pressure</span>
                </div>
              </div>
              <div className="detail-item">
