import React, { useState, useEffect, useRef } from 'react';
import './App.css';

// Weather Icons mapping
const weatherIcons = {
  '01d': '‚òÄÔ∏è', '01n': 'üåô',
  '02d': '‚õÖ', '02n': '‚òÅÔ∏è',
  '03d': '‚òÅÔ∏è', '03n': '‚òÅÔ∏è',
  '04d': '‚òÅÔ∏è', '04n': '‚òÅÔ∏è',
  '09d': 'üåßÔ∏è', '09n': 'üåßÔ∏è',
  '10d': 'üå¶Ô∏è', '10n': 'üåßÔ∏è',
  '11d': '‚õàÔ∏è', '11n': '‚õàÔ∏è',
  '13d': '‚ùÑÔ∏è', '13n': '‚ùÑÔ∏è',
  '50d': 'üå´Ô∏è', '50n': 'üå´Ô∏è'
};

// Format time to 12-hour format
const formatTime = (timeString) => {
  if (!timeString) return '';
  const time = timeString.split(' ')[1];
  const [hourStr, minutes] = time.split(':');
  const hour = parseInt(hourStr, 10);
  const ampm = hour >= 12 ? 'PM' : 'AM';
  const hour12 = hour % 12 || 12;
  return `${hour12}:${minutes} ${ampm}`;
};

function App() {
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState(null);
  const [city, setCity] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const suggestionsRef = useRef(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const API_KEY = '9a983165173b4de69a1170742252411';
  const API_BASE_URL = 'http://api.weatherapi.com/v1';

  // Debounce function to limit API calls
  const debounce = (func, delay) => {
    let timeoutId;
    return function(...args) {
      clearTimeout(timeoutId);
      timeoutId = setTimeout(() => {
        func.apply(this, args);
      }, delay);
    };
  };

  const fetchWeather = async (cityName) => {
    if (!cityName) return;

    setLoading(true);
    setError('');

    try {
      // Current weather
      const currentResponse = await fetch(
        `${API_BASE_URL}/forecast.json?key=${API_KEY}&q=${encodeURIComponent(cityName)}&days=6&aqi=no&alerts=no`
      );
      const data = await currentResponse.json();

      if (!data.error) {
        // Current weather data
        const weatherData = {
          city: data.location.name,
          country: data.location.country,
          temp: Math.round(data.current.temp_c),
          description: data.current.condition.text,
          icon: `https:${data.current.condition.icon}`,
          humidity: data.current.humidity,
          wind: data.current.wind_kph,
          wind_dir: data.current.wind_dir,
          pressure: data.current.pressure_mb,
          visibility: data.current.vis_km,
          feels_like: Math.round(data.current.feelslike_c),
          last_updated: data.current.last_updated
        };

        // Forecast data (next 5 days, skip today)
        if (data.forecast && data.forecast.forecastday.length > 1) {
          const forecastData = data.forecast.forecastday.slice(1).map(day => ({
            date: day.date,
            day: new Date(day.date).toLocaleDateString('en-US', { weekday: 'short' }),
            maxtemp_c: Math.round(day.day.maxtemp_c),
            mintemp_c: Math.round(day.day.mintemp_c),
            condition: day.day.condition.text,
            icon: `https:${day.day.condition.icon}`,
            avghumidity: day.day.avghumidity,
            maxwind_kph: day.day.maxwind_kph,
            daily_chance_of_rain: day.day.daily_chance_of_rain,
            daily_chance_of_snow: day.day.daily_chance_of_snow
          }));
          setForecast(forecastData);
        }

        setWeather(weatherData);
        setCity('');
      } else {
        setError(currentData.error?.message || 'City not found.');
      }
    } catch (err) {
      setError('Failed to fetch weather data');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // Fetch city suggestions
  const fetchSuggestions = async (query) => {
    if (!query.trim()) {
      setSuggestions([]);
      return;
    }
    
    try {
      const response = await fetch(
        `${API_BASE_URL}/search.json?key=${API_KEY}&q=${encodeURIComponent(query)}`
      );
      const data = await response.json();
      setSuggestions(data || []);
    } catch (error) {
      console.error('Error fetching suggestions:', error);
      setSuggestions([]);
    }
  };

  // Debounced version of fetchSuggestions
  const debouncedFetchSuggestions = useRef(
    debounce((query) => fetchSuggestions(query), 300)
  ).current;

  useEffect(() => {
    return () => {
      debouncedFetchSuggestions.cancel?.();
    };
  }, [debouncedFetchSuggestions]);

  const handleSearch = (e) => {
    e.preventDefault();
    if (city.trim()) {
      fetchWeather(city);
      setShowSuggestions(false);
    }
  };

  const handleSuggestionClick = (suggestion) => {
    setCity(suggestion.name);
    setShowSuggestions(false);
    fetchWeather(suggestion.name);
  };

  // Close suggestions when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (suggestionsRef.current && !suggestionsRef.current.contains(event.target)) {
        setShowSuggestions(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const getWeatherEmoji = (iconUrl) => {
    if (!iconUrl) return 'üå°Ô∏è';
    const iconCode = iconUrl.split('/').pop().split('.')[0];
    return weatherIcons[iconCode] || 'üå°Ô∏è';
  };

  return (
    <div className="app">
      <div className="container">
        <h1>Weather Forecast</h1>

        {/* Search Input */}
        <form onSubmit={handleSearch} className="search-box">
          <div className="search-container" ref={suggestionsRef}>
            <input
              type="text"
              value={city}
              onChange={(e) => {
                setCity(e.target.value);
                debouncedFetchSuggestions(e.target.value);
                setShowSuggestions(true);
              }}
              onFocus={() => setShowSuggestions(true)}
              placeholder="Enter city name"
              className="search-input"
              autoComplete="off"
            />
            {showSuggestions && suggestions.length > 0 && (
              <ul className="suggestions-list">
                {suggestions.map((suggestion, index) => (
                  <li 
                    key={`${suggestion.id || index}-${suggestion.name}`}
                    className="suggestion-item"
                    onClick={() => handleSuggestionClick(suggestion)}
                  >
                    {suggestion.name}, {suggestion.region ? `${suggestion.region}, ` : ''}{suggestion.country}
                  </li>
                ))}
              </ul>
            )}
          </div>
          <button type="submit" disabled={loading || !city.trim()}>
            {loading ? <span className="loading-dots">Searching</span> : 'Get Weather'}
          </button>
        </form>

        {error && <div className="error">{error}</div>}

        {/* Loading */}
        {loading ? (
          <div className="loading">Loading weather data...</div>
        ) : weather ? (
          <>
            {/* Weather Card */}
            <div className="weather-card">
              <div className="location">
                <h2>{weather.city}, {weather.country}</h2>
                <p>Last updated: {formatTime(weather.last_updated)}</p>
              </div>

              <div className="weather-main">
                <div className="weather-icon">
                  <img
                    src={weather.icon}
                    alt={weather.description}
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.style.display = 'none';
                      e.target.parentNode.innerHTML = getWeatherEmoji(weather.icon);
                    }}
                  />
                </div>
                <div className="temp">{weather.temp}¬∞</div>
              </div>

              <div className="weather-desc">{weather.description}</div>

              <div className="weather-details">
                <div className="detail-item">
                  <div className="icon">üå°Ô∏è</div>
                  <div className="info">
                    <span className="value">{weather.feels_like}¬∞</span>
                    <span className="label">Feels Like</span>
                  </div>
                </div>

                <div className="detail-item">
                  <div className="icon">üíß</div>
                  <div className="info">
                    <span className="value">{weather.humidity}%</span>
                    <span className="label">Humidity</span>
                  </div>
                </div>

                <div className="detail-item">
                  <div className="icon">üí®</div>
                  <div className="info">
                    <span className="value">{weather.wind} km/h</span>
                    <span className="label">{weather.wind_dir} Wind</span>
                  </div>
                </div>

                <div className="detail-item">
                  <div className="icon">üìä</div>
                  <div className="info">
                    <span className="value">{weather.pressure} mb</span>
                    <span className="label">Pressure</span>
                  </div>
                </div>

                <div className="detail-item">
                  <div className="icon">üëÅÔ∏è</div>
                  <div className="info">
                    <span className="value">{weather.visibility} km</span>
                    <span className="label">Visibility</span>
                  </div>
                </div>
              </div>
            </div>

            {/* 5-Day Forecast */}
            {forecast && (
              <div className="forecast-container">
                <h3>5-Day Forecast</h3>
                <div className="forecast-grid">
                  {forecast.map((day, index) => (
                    <div key={index} className="forecast-day">
                      <div className="forecast-day-name">{day.day}</div>
                      <div className="forecast-icon">
                        <img 
                          src={day.icon} 
                          alt={day.condition}
                          onError={(e) => {
                            e.target.onerror = null;
                            e.target.style.display = 'none';
                            e.target.parentNode.innerHTML = getWeatherEmoji(day.icon);
                          }}
                        />
                      </div>
                      <div className="forecast-temps">
                        <span className="forecast-temp-max">{day.maxtemp_c}¬∞</span>
                        <span className="forecast-temp-min">{day.mintemp_c}¬∞</span>
                      </div>
                      <div className="forecast-details">
                        <span className="forecast-precipitation">
                          üíß {Math.max(day.daily_chance_of_rain, day.daily_chance_of_snow)}%
                        </span>
                        <span className="forecast-wind">üí® {day.maxwind_kph} km/h</span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </>
        ) : (
          <div className="welcome-message">
            <div className="welcome-card">
              <h3>Welcome to Weather App</h3>
              <p>Enter a city name to check the current weather</p>

              <div className="example-cities">
                <p>Try searching:</p>
                <div className="city-tags">
                  <span onClick={() => fetchWeather('New York')}>New York</span>
                  <span onClick={() => fetchWeather('London')}>London</span>
                  <span onClick={() => fetchWeather('Tokyo')}>Tokyo</span>
                  <span onClick={() => fetchWeather('Sydney')}>Sydney</span>
                </div>
              </div>

            </div>
          </div>
        )}

        <footer>
          <p>
            Powered by{" "}
            <a
              href="https://www.weatherapi.com/"
              target="_blank"
              rel="noopener noreferrer"
            >
              WeatherAPI.com
            </a>
          </p>
        </footer>
      </div>
    </div>
  );
}

export default App;
