import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [weather, setWeather] = useState(null);
  const [city, setCity] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const API_KEY = '9a983165173b4de69a1170742252411';
  const API_URL = `http://api.weatherapi.com/v1/current.json?key=${API_KEY}&aqi=yes`;

  const fetchWeather = async (cityName) => {
    if (!cityName) return;
    
    setLoading(true);
    setError('');
    
    try {
      const response = await fetch(`${API_URL}&q=${encodeURIComponent(cityName)}`);
      const data = await response.json();
      
      if (!data.error) {
        setWeather({
          city: data.location.name,
          country: data.location.country,
          temp: Math.round(data.current.temp_c),
          description: data.current.condition.text,
          icon: data.current.condition.icon,
          humidity: data.current.humidity,
          wind: data.current.wind_kph,
          feels_like: Math.round(data.current.feelslike_c),
          wind_dir: data.current.wind_dir,
          pressure: data.current.pressure_mb,
          visibility: data.current.vis_km,
          last_updated: data.current.last_updated
        });
      } else {
        setError(data.error.message || 'City not found. Please try again.');
      }
    } catch (err) {
      setError('Failed to fetch weather data');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchWeather(city);
  };

  // Load default city on first render
  useEffect(() => {
    fetchWeather('London');
  }, []);

  return (
    <div className="app">
      <div className="container">
        <h1>Weather App</h1>
        
        <form onSubmit={handleSubmit} className="search-box">
          <input
            type="text"
            placeholder="Enter city name"
            value={city}
            onChange={(e) => setCity(e.target.value)}
          />
          <button type="submit" disabled={loading}>
            {loading ? 'Searching...' : 'Search'}
          </button>
        </form>

        {error && <div className="error">{error}</div>}

        {weather && !loading && (
          <div className="weather-card">
            <h2>{weather.city}, {weather.country}</h2>
            <div className="weather-main">
              <img 
                src={`http://openweathermap.org/img/wn/${weather.icon}@2x.png`} 
                alt={weather.description}
              />
              <div className="temp">{weather.temp}¬∞C</div>
            </div>
            <div className="weather-desc">{weather.description}</div>
            <div className="weather-details">
              <div>üå°Ô∏è Feels like: {weather.feels_like}¬∞C</div>
              <div>üíß Humidity: {weather.humidity}%</div>
              <div>üí® Wind: {weather.wind} km/h {weather.wind_dir}</div>
              <div>üìä Pressure: {weather.pressure} mb</div>
              <div>üëÅÔ∏è Visibility: {weather.visibility} km</div>
              <div>üïí Updated: {weather.last_updated}</div>
            </div>
          </div>
        )}

        <footer>
          <p>Data provided by OpenWeatherMap</p>
        </footer>
      </div>
    </div>
  );
}

export default App;
