import React, { useState, useEffect } from 'react';
import './App.css';

// Weather Icons mapping
const weatherIcons = {
  '01d': 'â˜€ï¸', '01n': 'ðŸŒ™',
  '02d': 'â›…', '02n': 'â˜ï¸',
  '03d': 'â˜ï¸', '03n': 'â˜ï¸',
  '04d': 'â˜ï¸', '04n': 'â˜ï¸',
  '09d': 'ðŸŒ§ï¸', '09n': 'ðŸŒ§ï¸',
  '10d': 'ðŸŒ¦ï¸', '10n': 'ðŸŒ§ï¸',
  '11d': 'â›ˆï¸', '11n': 'â›ˆï¸',
  '13d': 'â„ï¸', '13n': 'â„ï¸',
  '50d': 'ðŸŒ«ï¸', '50n': 'ðŸŒ«ï¸'
};

// Format time to 12-hour format
const formatTime = (timeString) => {
  if (!timeString) return '';
  const time = timeString.split(' ')[1]; // Get only the time part
  const [hours, minutes] = time.split(':');
  const hour = parseInt(hours, 10);
  const ampm = hour >= 12 ? 'PM' : 'AM';
  const hour12 = hour % 12 || 12;
  return `${hour12}:${minutes} ${ampm}`;
};

function App() {
  const [weather, setWeather] = useState(null);
  const [city, setCity] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const API_KEY = 'edeec0279d96c3f509d9efe7a8f578f1';
  const API_URL = `https://api.openweathermap.org/data/2.5/weather?appid=${API_KEY}&units=metric`;

  const fetchWeather = async (cityName) => {
    if (!cityName) return;
    
    setLoading(true);
    setError('');
    
    try {
      const response = await fetch(`${API_URL}&q=${encodeURIComponent(cityName)}`);
      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || 'Failed to fetch weather data');
      }
      
      // Convert wind speed from m/s to km/h and format to 1 decimal place
      const windSpeed = (data.wind?.speed * 3.6).toFixed(1);
      
      setWeather({
        city: data.name,
        country: data.sys?.country,
        temp: Math.round(data.main?.temp),
        condition: data.weather?.[0]?.main,
        description: data.weather?.[0]?.description,
        icon: `https://openweathermap.org/img/wn/${data.weather?.[0]?.icon}@2x.png`,
        feels_like: Math.round(data.main?.feels_like),
        humidity: data.main?.humidity,
        wind: windSpeed,
        wind_deg: data.wind?.deg,
        pressure: data.main?.pressure,
        visibility: data.visibility ? (data.visibility / 1000).toFixed(1) : 'N/A',
        sunrise: data.sys?.sunrise ? new Date(data.sys.sunrise * 1000).toLocaleTimeString() : 'N/A',
        sunset: data.sys?.sunset ? new Date(data.sys.sunset * 1000).toLocaleTimeString() : 'N/A',
        clouds: data.clouds?.all,
        last_updated: new Date().toLocaleTimeString()
      });
    } catch (err) {
      setError('Failed to fetch weather data');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchWeather(city);
  };

  // Empty dependency array means this effect runs once on mount
  useEffect(() => {
    // No default city fetch on initial load
  }, []);

  // Get weather emoji based on icon code
  const getWeatherEmoji = (iconUrl) => {
    if (!iconUrl) return '';
    const iconCode = iconUrl.split('/').pop().split('.')[0];
    return weatherIcons[iconCode] || '';
  };

  return (
    <div className="app">
      <div className="container">
        <h1>Weather Forecast</h1>
        
        <form onSubmit={handleSubmit} className="search-box">
          <input
            type="text"
            placeholder="Enter city name..."
            value={city}
            onChange={(e) => setCity(e.target.value)}
            disabled={loading}
          />
          <button type="submit" disabled={loading || !city.trim()}>
            {loading ? (
              <span className="loading-dots">Searching</span>
            ) : (
              'Get Weather'
            )}
          </button>
        </form>

        {error && (
          <div className="error">
            <span></span> {error}
          </div>
        )}

        {loading && !weather ? (
          <div className="loading">
            <div className="loading-spinner"></div>
            <p>Fetching weather data...</p>
          </div>
        ) : weather ? (
          <div className="weather-card">
            <div className="location">
              <h2>{weather.city}, {weather.country}</h2>
              <p>Last updated: {formatTime(weather.last_updated)}</p>
            </div>
            
            <div className="weather-main">
              <div className="weather-icon">
                <img 
                  src={weather.icon} 
                  alt={weather.description}
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.style.display = 'none';
                    e.target.parentNode.innerHTML = getWeatherEmoji(weather.icon);
                  }}
                />
              </div>
              <div className="temp">{weather.temp}</div>
            </div>
            
            <div className="weather-desc">
              {weather.description}
            </div>
            
            <div className="weather-details">
              <div className="detail-item">
                <div className="icon"></div>
                <div className="info">
                  <span className="value">{weather.feels_like}</span>
                  <span className="label">Feels Like</span>
                </div>
              </div>
              <div className="detail-item">
                <div className="icon"></div>
                <div className="info">
                  <span className="value">{weather.humidity}%</span>
                  <span className="label">Humidity</span>
                </div>
              </div>
              <div className="detail-item">
                <div className="icon"></div>
                <div className="info">
                  <span className="value">{weather.wind} km/h</span>
                  <span className="label">{weather.wind_deg ? `${weather.wind_deg}` : 'Wind'}</span>
                </div>
              </div>
              <div className="detail-item">
                <div className="icon"></div>
                <div className="info">
                  <span className="value">{weather.pressure} hPa</span>
                  <span className="label">Pressure</span>
                </div>
              </div>
              <div className="detail-item">
                <div className="icon"></div>
                <div className="info">
                  <span className="value">{weather.visibility} km</span>
                  <span className="label">Visibility</span>
                </div>
              </div>
              {weather.clouds !== undefined && (
                <div className="detail-item">
                  <div className="icon"></div>
                  <div className="info">
                    <span className="value">{weather.clouds}%</span>
                    <span className="label">Clouds</span>
                  </div>
                </div>
              )}
              {weather.sunrise !== 'N/A' && (
                <div className="detail-item">
                  <div className="icon"></div>
                  <div className="info">
                    <span className="value">{weather.sunrise}</span>
                    <span className="label">Sunrise</span>
                  </div>
                </div>
              )}
              {weather.sunset !== 'N/A' && (
                <div className="detail-item">
                  <div className="icon"></div>
                  <div className="info">
                    <span className="value">{weather.sunset}</span>
                    <span className="label">Sunset</span>
                  </div>
                </div>
              )}
            </div>
            
          </div>
        ) : (
          <div className="welcome-message">
            <div className="welcome-card">
              <h3>Welcome to Weather App</h3>
              <p>Enter a city name to check the current weather</p>
              <div className="example-cities">
                <p>Try searching for:</p>
                <div className="city-tags">
                  <span onClick={() => fetchWeather('New York')}>New York</span>
                  <span onClick={() => fetchWeather('London')}>London</span>
                  <span onClick={() => fetchWeather('Tokyo')}>Tokyo</span>
                  <span onClick={() => fetchWeather('Sydney')}>Sydney</span>
                </div>
              </div>
            </div>
          </div>
        )}

        <footer>
          <p>
            Powered by <a href="https://www.weatherapi.com/" target="_blank" rel="noopener noreferrer">WeatherAPI.com</a>
          </p>
        </footer>
      </div>
    </div>
  );
}

export default App;
