import React, { useState, useEffect, useRef, useCallback } from 'react';
import './App.css';

// Weather Icons mapping
const weatherIcons = {
  '01d': '‚òÄÔ∏è', '01n': 'üåô',
  '02d': '‚õÖ', '02n': '‚òÅÔ∏è',
  '03d': '‚òÅÔ∏è', '03n': '‚òÅÔ∏è',
  '04d': '‚òÅÔ∏è', '04n': '‚òÅÔ∏è',
  '09d': 'üåßÔ∏è', '09n': 'üåßÔ∏è',
  '10d': 'üå¶Ô∏è', '10n': 'üåßÔ∏è',
  '11d': '‚õàÔ∏è', '11n': '‚õàÔ∏è',
  '13d': '‚ùÑÔ∏è', '13n': '‚ùÑÔ∏è',
  '50d': 'üå´Ô∏è', '50n': 'üå´Ô∏è'
};

// Format time to 12-hour format
const formatTime = (timeString) => {
  if (!timeString) return '';
  const time = timeString.split(' ')[1];
  const [hourStr, minutes] = time.split(':');
  const hour = parseInt(hourStr, 10);
  const ampm = hour >= 12 ? 'PM' : 'AM';
  const hour12 = hour % 12 || 12;
  return `${hour12}:${minutes} ${ampm}`;
};

function App() {
  const [weather, setWeather] = useState(null);
  const [city, setCity] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const suggestionsRef = useRef(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [weatherCondition, setWeatherCondition] = useState('clear');
  const particlesRef = useRef(null);

  const API_KEY = '9a983165173b4de69a1170742252411';
  const API_BASE_URL = 'http://api.weatherapi.com/v1';

  // Debounce function to limit API calls
  const debounce = (func, delay) => {
    let timeoutId;
    return function(...args) {
      clearTimeout(timeoutId);
      timeoutId = setTimeout(() => {
        func.apply(this, args);
      }, delay);
    };
  };

  // Function to determine weather condition from description
  const getWeatherCondition = (description) => {
    const desc = description.toLowerCase();
    if (desc.includes('rain') || desc.includes('drizzle') || desc.includes('shower')) return 'rain';
    if (desc.includes('snow') || desc.includes('sleet') || desc.includes('blizzard')) return 'snow';
    if (desc.includes('thunder') || desc.includes('storm')) return 'thunderstorm';
    if (desc.includes('cloud') || desc.includes('overcast')) return 'cloudy';
    if (desc.includes('fog') || desc.includes('mist') || desc.includes('haze')) return 'fog';
    return 'clear';
  };

  // Create particles based on weather condition
  const createParticles = useCallback((condition) => {
    if (!particlesRef.current) return;
    
    // Clear existing particles
    particlesRef.current.innerHTML = '';
    
    const particleCount = condition === 'clear' ? 10 : 
                         condition === 'cloudy' ? 15 : 
                         condition === 'rain' ? 80 : 
                         condition === 'snow' ? 100 : 30;
    
    for (let i = 0; i < particleCount; i++) {
      const particle = document.createElement('div');
      particle.className = 'particle';
      
      // Set particle properties based on weather condition
      switch(condition) {
        case 'rain':
          particle.style.width = '2px';
          particle.style.height = Math.random() * 20 + 10 + 'px';
          particle.style.background = 'rgba(174, 194, 224, 0.8)';
          particle.style.left = Math.random() * 100 + 'vw';
          particle.style.animation = `rain ${Math.random() * 0.5 + 0.5}s linear infinite`;
          particle.style.animationDelay = Math.random() * 2 + 's';
          break;
          
        case 'snow':
          particle.style.width = Math.random() * 5 + 3 + 'px';
          particle.style.height = particle.style.width;
          particle.style.background = 'rgba(255, 255, 255, 0.8)';
          particle.style.borderRadius = '50%';
          particle.style.left = Math.random() * 100 + 'vw';
          particle.style.animation = `snow ${Math.random() * 10 + 5}s linear infinite`;
          particle.style.animationDelay = Math.random() * 5 + 's';
          break;
          
        case 'thunderstorm':
          if (i % 5 === 0) {
            // Lightning effect
            particle.style.width = '2px';
            particle.style.height = '100%';
            particle.style.background = 'rgba(255, 255, 200, 0.8)';
            particle.style.left = Math.random() * 100 + 'vw';
            particle.style.animation = `lightning ${Math.random() * 10 + 10}s linear infinite`;
            particle.style.animationDelay = Math.random() * 5 + 's';
          } else {
            // Rain for thunderstorm
            particle.style.width = '1.5px';
            particle.style.height = Math.random() * 15 + 8 + 'px';
            particle.style.background = 'rgba(150, 170, 200, 0.8)';
            particle.style.left = Math.random() * 100 + 'vw';
            particle.style.animation = `rain ${Math.random() * 0.4 + 0.3}s linear infinite`;
            particle.style.animationDelay = Math.random() * 2 + 's';
          }
          break;
          
        case 'cloudy':
          particle.style.width = Math.random() * 100 + 50 + 'px';
          particle.style.height = '60px';
          particle.style.background = 'rgba(255, 255, 255, 0.1)';
          particle.style.borderRadius = '50%';
          particle.style.left = Math.random() * 100 + 'vw';
          particle.style.top = Math.random() * 40 + 10 + 'vh';
          particle.style.animation = `cloud ${Math.random() * 40 + 20}s linear infinite`;
          particle.style.animationDelay = Math.random() * 10 + 's';
          break;
          
        case 'fog':
          particle.style.width = '100%';
          particle.style.height = Math.random() * 100 + 100 + 'px';
          particle.style.background = 'rgba(255, 255, 255, 0.05)';
          particle.style.left = '0';
          particle.style.top = Math.random() * 100 + 'vh';
          particle.style.animation = `fog ${Math.random() * 20 + 10}s linear infinite`;
          particle.style.animationDelay = Math.random() * 10 + 's';
          break;
          
        default: // clear
          particle.style.width = Math.random() * 3 + 1 + 'px';
          particle.style.height = particle.style.width;
          particle.style.background = 'rgba(255, 255, 255, 0.8)';
          particle.style.borderRadius = '50%';
          particle.style.left = Math.random() * 100 + 'vw';
          particle.style.animation = `float ${Math.random() * 20 + 10}s linear infinite`;
          particle.style.animationDelay = Math.random() * 5 + 's';
      }
      
      particlesRef.current.appendChild(particle);
    }
  }, []);
  
  // Update weather condition and particles when weather data changes
  useEffect(() => {
    if (weather) {
      const condition = getWeatherCondition(weather.description);
      setWeatherCondition(condition);
      createParticles(condition);
    }
  }, [weather, createParticles]);

  const fetchWeather = async (cityName) => {
    if (!cityName) return;

    setLoading(true);
    setError('');

    try {
      // Current weather
      const currentResponse = await fetch(
        `${API_BASE_URL}/current.json?key=${API_KEY}&q=${encodeURIComponent(cityName)}&aqi=yes`
      );
      const currentData = await currentResponse.json();

      if (!currentData.error) {
        const weatherData = {
          city: currentData.location.name,
          country: currentData.location.country,
          temp: Math.round(currentData.current.temp_c),
          description: currentData.current.condition.text,
          icon: `https:${currentData.current.condition.icon}`,
          humidity: currentData.current.humidity,
          wind: currentData.current.wind_kph,
          wind_dir: currentData.current.wind_dir,
          pressure: currentData.current.pressure_mb,
          visibility: currentData.current.vis_km,
          feels_like: Math.round(currentData.current.feelslike_c),
          last_updated: currentData.current.last_updated
        };

        setWeather(weatherData);
        setCity('');
      } else {
        setError(currentData.error?.message || 'City not found.');
      }
    } catch (err) {
      setError('Failed to fetch weather data');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // Fetch city suggestions
  const fetchSuggestions = async (query) => {
    if (!query.trim()) {
      setSuggestions([]);
      return;
    }
    
    try {
      const response = await fetch(
        `${API_BASE_URL}/search.json?key=${API_KEY}&q=${encodeURIComponent(query)}`
      );
      const data = await response.json();
      setSuggestions(data || []);
    } catch (error) {
      console.error('Error fetching suggestions:', error);
      setSuggestions([]);
    }
  };

  // Debounced version of fetchSuggestions
  const debouncedFetchSuggestions = useRef(
    debounce((query) => fetchSuggestions(query), 300)
  ).current;

  useEffect(() => {
    return () => {
      debouncedFetchSuggestions.cancel?.();
    };
  }, [debouncedFetchSuggestions]);

  const handleSearch = (e) => {
    e.preventDefault();
    if (city.trim()) {
      fetchWeather(city);
      setShowSuggestions(false);
    }
  };

  const handleSuggestionClick = (suggestion) => {
    setCity(suggestion.name);
    setShowSuggestions(false);
    fetchWeather(suggestion.name);
  };

  // Close suggestions when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (suggestionsRef.current && !suggestionsRef.current.contains(event.target)) {
        setShowSuggestions(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const getWeatherEmoji = (iconUrl) => {
    if (!iconUrl) return 'üå°Ô∏è';
    const iconCode = iconUrl.split('/').pop().split('.')[0];
    return weatherIcons[iconCode] || 'üå°Ô∏è';
  };

  // Set background class based on weather condition
  const backgroundClass = `weather-${weatherCondition}`;

  return (
    <div className={`app ${backgroundClass}`}>
      <div className="particles" ref={particlesRef}></div>
      <div className="container">
        <h1>Weather Forecast</h1>

        {/* Search Input */}
        <form onSubmit={handleSearch} className="search-box">
          <div className="search-container" ref={suggestionsRef}>
            <input
              type="text"
              value={city}
              onChange={(e) => {
                setCity(e.target.value);
                debouncedFetchSuggestions(e.target.value);
                setShowSuggestions(true);
              }}
              onFocus={() => setShowSuggestions(true)}
              placeholder="Enter city name"
              className="search-input"
              autoComplete="off"
            />
            {showSuggestions && suggestions.length > 0 && (
              <ul className="suggestions-list">
                {suggestions.map((suggestion, index) => (
                  <li 
                    key={`${suggestion.id || index}-${suggestion.name}`}
                    className="suggestion-item"
                    onClick={() => handleSuggestionClick(suggestion)}
                  >
                    {suggestion.name}, {suggestion.region ? `${suggestion.region}, ` : ''}{suggestion.country}
                  </li>
                ))}
              </ul>
            )}
          </div>
          <button type="submit" disabled={loading || !city.trim()}>
            {loading ? <span className="loading-dots">Searching</span> : 'Get Weather'}
          </button>
        </form>

        {error && <div className="error">{error}</div>}

        {/* Loading */}
        {loading ? (
          <div className="loading">Loading weather data...</div>
        ) : weather ? (
          <>
            {/* Weather Card */}
            <div className="weather-card">
              <div className="location">
                <h2>{weather.city}, {weather.country}</h2>
                <p>Last updated: {formatTime(weather.last_updated)}</p>
              </div>

              <div className="weather-main">
                <div className="weather-icon">
                  <img
                    src={weather.icon}
                    alt={weather.description}
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.style.display = 'none';
                      e.target.parentNode.innerHTML = getWeatherEmoji(weather.icon);
                    }}
                  />
                </div>
                <div className="temp">{weather.temp}¬∞</div>
              </div>

              <div className="weather-desc">{weather.description}</div>

              <div className="weather-details">
                <div className="detail-item">
                  <div className="icon">üå°Ô∏è</div>
                  <div className="info">
                    <span className="value">{weather.feels_like}¬∞</span>
                    <span className="label">Feels Like</span>
                  </div>
                </div>

                <div className="detail-item">
                  <div className="icon">üíß</div>
                  <div className="info">
                    <span className="value">{weather.humidity}%</span>
                    <span className="label">Humidity</span>
                  </div>
                </div>

                <div className="detail-item">
                  <div className="icon">üí®</div>
                  <div className="info">
                    <span className="value">{weather.wind} km/h</span>
                    <span className="label">{weather.wind_dir} Wind</span>
                  </div>
                </div>

                <div className="detail-item">
                  <div className="icon">üìä</div>
                  <div className="info">
                    <span className="value">{weather.pressure} mb</span>
                    <span className="label">Pressure</span>
                  </div>
                </div>

                <div className="detail-item">
                  <div className="icon">üëÅÔ∏è</div>
                  <div className="info">
                    <span className="value">{weather.visibility} km</span>
                    <span className="label">Visibility</span>
                  </div>
                </div>
              </div>
            </div>

          </>
        ) : (
          <div className="welcome-message">
            <div className="welcome-card">
              <h3>Welcome to Weather App</h3>
              <p>Enter a city name to check the current weather</p>

              <div className="example-cities">
                <p>Try searching:</p>
                <div className="city-tags">
                  <span onClick={() => fetchWeather('New York')}>New York</span>
                  <span onClick={() => fetchWeather('London')}>London</span>
                  <span onClick={() => fetchWeather('Tokyo')}>Tokyo</span>
                  <span onClick={() => fetchWeather('Sydney')}>Sydney</span>
                </div>
              </div>

            </div>
          </div>
        )}

        <footer>
          <p>
            Powered by{" "}
            <a
              href="https://www.weatherapi.com/"
              target="_blank"
              rel="noopener noreferrer"
            >
              WeatherAPI.com
            </a>
          </p>
        </footer>
      </div>
    </div>
  );
}

export default App;
